const validateSignInUpForm=formValue=>{if(!formValue.email||formValue.email===""){showError("Please provide an email");return false}if(!formValue.email.match(/^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/g)){showError("Please provide a valid email");return false}if(!formValue.password||formValue.password===""){showError("Please provide a password");return false}return true};const showError=errorMessage=>{const body=document.getElementsByTagName("body")[0];console.log(body);const randomNumber=Math.random();const id=`toast-${randomNumber}`;body.insertAdjacentHTML("beforeend",`    
    <div id="${id}" class="toast errorToast position-fixed align-items-center text-bg-danger show border-0" role="alert" aria-live="assertive" aria-atomic="true">
        <div class="d-flex">
            <div class="toast-body">
                ${errorMessage}
            </div>
            <button type="button" class="btn-close btn-close-white me-2 m-auto" data-bs-dismiss="toast"
                aria-label="Close"></button>
        </div>
    </div>`)};const showHidePassword=()=>{const icon=document.getElementById("showIcon");const passwordInput=document.getElementById("passwordInput");if(icon.classList.contains("fa-eye")){icon.classList.remove("fa-eye");icon.classList.add("fa-eye-slash");passwordInput.type="text"}else{icon.classList.remove("fa-eye-slash");icon.classList.add("fa-eye");passwordInput.type="password"}};const handleSignin=async()=>{const formValue={email:document.getElementById("emailInput").value,password:document.getElementById("passwordInput").value};const formDataValidated=validateSignInUpForm(formValue);if(formDataValidated){const response=await fetch("/signin",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({email:formValue.email,password:formValue.password})});console.log(response);if(response.status!==200){const responseBody=await response.json();console.log(responseBody);showError(responseBody.error)}else{window.location="/home"}}};const signInButton=document.getElementById("signinBtn");signInButton.addEventListener("click",handleSignin);const showBtn=document.getElementById("showButton");showBtn.addEventListener("click",showHidePassword);const logoHomeBtn=document.getElementById("logo");logoHomeBtn.addEventListener("click",()=>{window.location="/"});